package com.rendonsoft.testtotalplay.utils.networkimport com.jakewharton.retrofit2.adapter.kotlin.coroutines.CoroutineCallAdapterFactoryimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryclass RetrofitCustom {    private companion object{        const val URL_BASE = "https://restcountries.com/v3.1/"    }    private val retrofit = Retrofit            .Builder()            .baseUrl(URL_BASE)            .addConverterFactory(GsonConverterFactory.create())            .addCallAdapterFactory(CoroutineCallAdapterFactory())            .build()    fun getRetrotif() = retrofit;}